import React, {Component} from 'react';
import styled from 'styled-components';

const Styles = styled.div`
    .component-quantity-input {
        display: block;
    }

    .component-quantity-input * {
        display: inline-block;
    }

    .component-quantity-input input {
        padding: 5px;
        text-align: center;
        width: 25px;
    }

    .component-quantity-input span {
        margin-left: 8px;
        margin-right: 8px;
        background: silver; 
        padding: 5px;
        width: 15px;
        text-align: center;
        cursor: pointer;
    }   
`;

export default class QuantityInput extends Component {
    constructor(props) {
        super(props);
        this.state = {
            quantity: this.props.quantity
        };
    }
    getInitialState() {
        return { quantity: 1 };
    };

    handleIncrement() {
        this.setState({ quantity: this.state.quantity + 1 });
    };

    handleDecrement() {
        if (this.state.quantity > 1) {
            this.setState({ quantity: this.state.quantity - 1 });    
        }
    };

    handleChange(e) {
        var value = e.target.value.replace(/[^0-9]/, '');
        value = (value == '' ? 1 : value);
        value = parseInt(value);

        this.setState({ quantity: value });
    };
    render() {
        return (
            <Styles>
                <div className="component-quantity-input">
                    <span onClick={this.handleDecrement}>-</span>
                    <input type="text" value={this.state.quantity} onChange={this.handleChange} />
                    <span onClick={this.handleIncrement}>+</span>
                </div>
            </Styles>
        )
    }
}
