import React, {Component} from 'react';
import styled from 'styled-components';

const Styles = styled.div`

    .component-quantity-input input{
        padding: 5px;
        text-align: center;
        width: 60px; 
        height: 30px;
    }
`;

export default class QuantityInput extends Component {
    constructor(props) {
        super(props);
        this.state = {
            quantity: this.props.quantity
        };
    }
    getInitialState() {
        return { quantity: 1 };
    };

    handleIncrement() {
        this.setState({ quantity: this.state.quantity + 1 });
    };

    handleDecrement() {
        if (this.state.quantity > 1) {
            this.setState({ quantity: this.state.quantity - 1 });    
        }
    };

    handleChange(e) {
        var value = e.target.value.replace(/[^0-9]/, '');
        value = (value == '' ? 1 : value);
        value = parseInt(value);

        this.setState({ quantity: value });
    };
    render() {
        return (
            <Styles>
                <div className="component-quantity-input">
                    <input type="number" value={2} onChange={this.handleChange} />
                </div>
            </Styles>
        )
    }
}
