import React, { Component } from 'react';
import {ListGroup, Card, Button} from 'react-bootstrap';
import data from '../data';

export class ProductList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            hover: false
        };
    }
    render() {
        var icon = product.image;
        return (
            <ListGroup horizontal style={{flexWrap:"wrap", justifyContent: "space-evenly"}}>
                {
                    data.products.map(product => 
                        <ListGroup.Item style={{borderStyle:"none"}}>  
                        <Card style={{ width: '18rem' }}>
                            <a href="/productdetails" style={{overflow:"hidden"}}>
                                <Card.Img 
                                variant="top" 
                                src={require(icon)} 
                                style={{objectFit:"cover", width:"100%", height:"15vw", transform: `${this.state.hover ? "scale(1.5)" : "scale(1)"}`, transition: "1s"}}
                                onMouseOver={() => this.setState({hover: true})}
                                onMouseOut={() => this.setState({hover: false})}
                                />
                            </a>
                            <Card.Body>
                                <a href="/productdetails">        
                                <Card.Title style={{color: "black"}}>{product.name}</Card.Title>
                                </a>
                                <Card.Text style={{color: "grey"}}>
                                {product.price} lei
                                </Card.Text>
                                <Button variant="primary">Adaugă în coș</Button>
                            </Card.Body>
                        </Card>         
                    </ListGroup.Item>
                        )
                }

            </ListGroup>
        )
    }
}
