import React, {Component} from 'react';
import styled from 'styled-components';
import { Button } from 'react-bootstrap';

const Styles = styled.div`

    .component-quantity-input input{
        padding: 5px;
        text-align: center;
        width: 60px; 
        height: 40px;
    }
`;

export default class QuantityInput extends Component {
    constructor(props) {
        super(props);
        this.state = {
            quantity: this.props.quantity
        };
    }

    changeQuantity() {
        this.setState({
            quantity: document.getElementById("calc").value
        })
    }
    render() {
        return (
            <Styles>
                <div className="component-quantity-input">
                    <input type="number" value={this.state.quantity} onChange={this.changeQuantity} id="calc"/>
                    <Button variant="danger" onClick={this.changeQuantity}>Modifica</Button>
                </div>
            </Styles>
        )
    }
}
