import React, {Component} from 'react';
import styled from 'styled-components';
import {ListGroup, Row, Col, Form, FormGroup, Button} from 'react-bootstrap';
import {Layout} from './components/Layout';
import { ReCaptcha } from 'react-recaptcha-google';

const Styles = styled.div`
    .list-group {
        backgroundColor: #A071A9;
    }

    .list-group .list-group-item {
        backgroundColor: #A071A9;
    }
`;

var styles = {
    item: {
        backgroundColor: "#A071A9",
        color: "white",
        borderColor: "white"
    }
}

export class Contact extends Component {
    constructor(props, context) {
        super(props, context);
        this.onLoadRecaptcha = this.onLoadRecaptcha.bind(this);
        this.verifyCallback = this.verifyCallback.bind(this);
    }
    componentDidMount() {
        if (this.captchaDemo) {
            console.log("started, just a second...")
            this.captchaDemo.reset();
        }
    }  

    onLoadRecaptcha() {
          if (this.captchaDemo) {
              this.captchaDemo.reset();
          }
    }  

    verifyCallback(recaptchaToken) {
        // Here you will get the final recaptchaToken!!!  
        console.log(recaptchaToken, "<= your recaptcha token")
    }
    render() {
        return (
            <Styles>
                <Layout>
                    <div style={{paddingTop: "40px"}}>
                        <Row>
                            <Col>
                                <h2 style={{color:"white"}}>Contactează-ne</h2>
                                <Form>
                                    <Form.Group>
                                        <Form.Label style={{color:"#F0F0F0"}}>Nume și prenume</Form.Label>
                                        <Form.Control type="text" />
                                    </Form.Group>
                                    <Form.Row>
                                        <Form.Group as={Col} controlId="formBasicEmail">
                                            <Form.Label style={{color:"#F0F0F0"}}>Adresă de email</Form.Label>
                                            <Form.Control type="email" placeholder="name@example.com" />
                                            <Form.Text className="text-muted" >
                                                Adresa ta este în siguranță cu noi
                                            </Form.Text>
                                        </Form.Group>
                                        <FormGroup as={Col}>
                                            <Form.Label style={{color:"#F0F0F0"}}>Număr de telefon</Form.Label>
                                            <Form.Control type="tel" placeholder="+40 (722) 222 222"/>
                                        </FormGroup>
                                    </Form.Row>
                                    <Form.Group>
                                        <Form.Label style={{color:"#F0F0F0"}}>Subiect</Form.Label>
                                        <Form.Control type="text" />
                                    </Form.Group>
                                    <Form.Group controlId="exampleForm.ControlTextarea1">
                                        <Form.Label style={{color:"#F0F0F0"}}>Mesaj</Form.Label>
                                        <Form.Control as="textarea" rows={10} style={{resize: "none"}}/>
                                    </Form.Group>
                                </Form>
                                <Button variant="primary">TRIMITE MESAJ</Button>
                                <ReCaptcha
                                    ref={(el) => {this.captchaDemo = el;}}
                                    size="compact"
                                    data-theme="dark"            
                                    render="explicit"
                                    sitekey="6LeO17IZAAAAAPgcOezijZ52srk6dS_fwhchPxRZ"
                                    onloadCallback={this.onLoadRecaptcha}
                                    verifyCallback={this.verifyCallback}
                                />
                            </Col>
                            <Col>
                                <ListGroup variant="flush" className="list-group">
                                        <ListGroup.Item className="list-group-item" style={styles.item}>
                                        <p><strong>Tel: </strong>+40 (784) 284 243</p>
                                        <p><strong>SMS: </strong>+40 (784) 284 243</p>
                                        <p><strong>Whatsapp: </strong>+40 (784) 284 243</p>
                                        <p><strong>Email: </strong>comenzi@florariebuzau.ro</p>
                                        </ListGroup.Item>
                                        <ListGroup.Item className="list-group-item" style={styles.item}>Dapibus ac facilisis in</ListGroup.Item>
                                        <ListGroup.Item className="list-group-item" style={styles.item}>Morbi leo risus</ListGroup.Item>
                                </ListGroup>
                            </Col>
                        </Row>
                    </div>
                </Layout>
            </Styles>
        )
    }

}
