import React, { useState, Component } from 'react';
import {Nav, Navbar, Image, Form, FormControl, Button, NavDropdown, Modal, Alert} from 'react-bootstrap';
import styled from 'styled-components';
import Logo from '../assets/logo2.png';
import { Cart4, TelephoneForward} from 'react-bootstrap-icons';
import {SocialIcon} from 'react-social-icons';
import {ShoppingCart} from './ShoppingCart';

const Styles = styled.div`
    .navbar {
        background-color: #222;
    }

    .navbar-brand, .navbar-nav .nav-link {
        color: #bbb;

        &:hover {
            color:white;
        }
    }

    .search-box {
        opacity: 0.7;
        color: gray;

        &:focus {
            opacity:1;
            color: black;
        }
    }

    #search-button {
        background-color: purple;
        border-color: purple;

        &:hover {
            background-color: #9A2FA2;
            border-color: gray;
        }
    }

    #basic-navbar-dropdown {
        color: #bbb;

        &:hover {
            color:white;
        }
    }
`;

export class NavigationBar extends Component {

    render() {
        const [show, setShow] = useState(false);

        const handleClose = () => setShow(false);
        const handleShow = () => setShow(true);

        return (
            <Styles>
                <Navbar expand="lg">
                    <Navbar.Brand href="/">
                        <Image src={Logo} fluid width="200"/>
                    </Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="mr-auto">
                            <Nav.Item><Nav.Link href="/">Home</Nav.Link></Nav.Item>
                            <Nav.Item><Nav.Link href="/bucheteflori">Buchete flori</Nav.Link></Nav.Item>
                            <Nav.Item><Nav.Link href="/aranjamenteflori">Aranjamente flori</Nav.Link></Nav.Item>
                            <NavDropdown title="Nunta" id="basic-navbar-dropdown">
                                <NavDropdown.Item href="/nunta">Nunta</NavDropdown.Item>
                                <NavDropdown.Divider />
                                <NavDropdown.Item href="/buchetedemireasa">Buchete de Mireasa</NavDropdown.Item>
                                <NavDropdown.Item href="/lumanaridecununie">Lumanari de cununie</NavDropdown.Item>
                                <NavDropdown.Item href="/aranjamentefloralemasa">Aranjamente florale masa</NavDropdown.Item>
                                <NavDropdown.Item href="/aranjamentefloralemasini">Aranjamente florale masini</NavDropdown.Item>
                                <NavDropdown.Item href="/buchetedomnisoare">Buchete domnisoare de onoare</NavDropdown.Item>
                            </NavDropdown>
                            <Nav.Item><Nav.Link href="/botez">Botez</Nav.Link></Nav.Item>
                            <Nav.Item><Nav.Link href="/contact">Contact</Nav.Link></Nav.Item>
                        </Nav>
                        <SocialIcon url="https://www.facebook.com/buzauflori" style={{ height: 35, width: 35, paddingRight: "50px" }} target="_blank"/>
                        <SocialIcon url="https://www.instagram.com/florariamedeea/?hl=en" style={{ height: 35, width: 35, paddingRight: "50px" }} target="_blank"/>
                        <TelephoneForward color="purple" size={30} /> 
                        <p style={{color:"#bbb", textAlign:"center", justifyContent:"center", paddingTop:"15px", paddingLeft:"10px", paddingRight:"20px"}}>
                        <a href="tel:+40 (784) 284 243">
                            +40 (784) 284 243
                            </a></p>
                        <Form inline>
                            <FormControl type="text" placeholder="Search" className="search-box mr-sm-2"/>
                            <Button variant="secondary" id="search-button">Search</Button>
                        </Form>
                        <Cart4 color="purple" size={50} style={{paddingLeft:"15px"}} onClick={handleShow}/>
                        <Modal
                                show={show}
                                onHide={handleClose}
                                backdrop="static"
                                keyboard={false}
                            >
                                <Modal.Header closeButton>
                                    <Modal.Title>Modal title</Modal.Title>
                                </Modal.Header>
                                <Modal.Body>
                                I will not close if you click outside me. Don't even try to press
                                escape key.
                                </Modal.Body>
                                <Modal.Footer>
                                    <Button variant="secondary" onClick={handleClose}>
                                        Close
                                    </Button>
                                    <Button variant="primary">Understood</Button>
                                </Modal.Footer>
                        </Modal>
                    </Navbar.Collapse>
                </Navbar>
            </Styles>
        )
    }

}
