import React from 'react';
import {ListGroup, Row, Col, OverlayTrigger, Popover} from 'react-bootstrap';
import styled from 'styled-components';
import {Truck, ShieldCheck, Check2Circle, Gift, CashStack} from 'react-bootstrap-icons'; 

const Styles = styled.div`
    .par: {
        fontSize: 16px; 
        color: grey;

        &:hover {
        color: red;
        }
    }
`;

var styles = {
    item: {
        width:"20%",
        textAlign: "center", 
        paddingRight: "0", 
        paddingLeft: "0"
    }
};
const popoverPlatiSecurizate = (
    <Popover id="popover-basic">
      <Popover.Title as="h3">Plăți Securizate</Popover.Title>
      <Popover.Content>
        Prin diverse metode de plată online și utilizarea unui protocol HTTPS, păstrăm datele dumneavoastră personale în siguranță.
      </Popover.Content>
    </Popover>
  );

const popoverLivrareInOre = (
    <Popover id="popover-basic">
      <Popover.Title as="h3">Livrare în 4 ore</Popover.Title>
      <Popover.Content>
        Livrare rapidă a produselor comandate oriunde în Buzău.
      </Popover.Content>
    </Popover>
  );

const popoverLivrareGratuita = (
    <Popover id="popover-basic">
      <Popover.Title as="h3">Livrare Gratuită</Popover.Title>
      <Popover.Content>
        Livrarea este gratuită în Buzău.
      </Popover.Content>
    </Popover>
  );

const popoverGarantie = (
    <Popover id="popover-basic">
      <Popover.Title as="h3">100% Discreție și Garanție</Popover.Title>
      <Popover.Content>
        Garantăm satisfacție pentru produsele ce vor ajunge în siguranță la dumneavoastră.
      </Popover.Content>
    </Popover>
  );

const popoverCadouri = (
    <Popover id="popover-basic">
      <Popover.Title as="h3">Felicitare Cadou</Popover.Title>
      <Popover.Content>
        La orice buchet, oferim și o felicitare cadou. Completați mesajul dorit la pasul următor.
      </Popover.Content>
    </Popover>
  );
export const Home = () => (

        <Row >

            <Col style={{maxWidth:"100%", width: "100%", justifyContent:"center", paddingRight: "0", paddingLeft: "0"}}>
            <Styles>
                <ListGroup horizontal>
                    <OverlayTrigger trigger="hover" placement="bottom" overlay={popoverPlatiSecurizate}>
                            <ListGroup.Item style={styles.item}>
                                <div id="item">
                                    <ShieldCheck size={40} color="grey" style={{paddingBottom:"10px"}}/>
                                    <p id="par">
                                        PLĂȚI SECURIZATE
                                    </p>
                                </div>
                            </ListGroup.Item>
                    </OverlayTrigger>
                    <OverlayTrigger trigger="hover" placement="bottom" overlay={popoverLivrareInOre}>
                        <ListGroup.Item style={styles.item}>
                            <Truck size={40} color="grey" style={{paddingBottom:"10px"}}/>
                            <p style={{fontSize: "16px", color: "grey"}}>LIVRARE ÎN 4 ORE</p>
                        </ListGroup.Item>
                    </OverlayTrigger>
                    <OverlayTrigger trigger="hover" placement="bottom" overlay={popoverLivrareGratuita}>
                        <ListGroup.Item style={styles.item}>
                            <CashStack size={40} color="grey" style={{paddingBottom:"10px"}}/>
                            <p style={{fontSize: "16px", color: "grey"}}>LIVRARE GRATUITĂ</p>
                        </ListGroup.Item>
                    </OverlayTrigger>
                    <OverlayTrigger trigger="hover" placement="bottom" overlay={popoverGarantie}>
                        <ListGroup.Item style={styles.item}>
                            <Check2Circle size={40} color="grey" style={{paddingBottom:"10px"}}/>
                            <p style={{fontSize: "16px", color: "grey"}}>100% DISCREȚIE ȘI GARANȚIE</p>
                        </ListGroup.Item>
                    </OverlayTrigger>
                    <OverlayTrigger trigger="hover" placement="bottom" overlay={popoverCadouri}>
                        <ListGroup.Item style={styles.item}>
                            <Gift size={40} color="grey" style={{paddingBottom:"10px"}}/>
                            <p style={{fontSize: "16px", color: "grey"}}>FELICITARE CADOU</p>
                        </ListGroup.Item> 
                    </OverlayTrigger>
                </ListGroup>
                </Styles>
            </Col>

        </Row>
)
